function DNA(length){this.fitness=0,this.length=length,this.genes=new Array(this.length);for(let i=0;i<this.genes.length;i++)this.genes[i]=get_random_char()}function get_random_char(){let start=32,end=126,rnd_num=randint(32,127);return String.fromCharCode(rnd_num)}function randint(min,max){return Math.floor(Math.random()*(max-min))+min}DNA.prototype.calculate_fitness=function(target_word){let score=0;for(let i=0;i<this.length;i++)this.genes[i]==target_word[i]&&(score+=1);return this.fitness=score/target_word.length,this.fitness},DNA.prototype.crossover=function(parent){let rnd_index=randint(0,this.length),child=new DNA(this.length);for(let i=0;i<child.length;i++)child.genes[i]=i<rnd_index?this.genes[i]:parent.genes[i];return child},DNA.prototype.mutation=function(mutation_per=.01){for(let i=0;i<this.length;i++){let rnd_num;Math.random()<mutation_per&&(this.genes[i]=get_random_char())}return this.genes};